sc_attr_e( $key ); ?>"><?php echo $field['label'] . apply_filters( 'submit_event_form_required_label', $field['required'] ? '<span class="require-field">*</span>' : ' <small>' . __( '(optional)', 'wp-event-manager' ) . '</small>', $field ); ?></label>					<?php					if(array_key_exists($key, $introtext)){						echo '<span class="introtext">'.$introtext[$key].'</span>';					}					?>				</div>				<div class="field <?php echo $field['required'] ? 'required-field' : ''; ?>">					<?php get_event_manager_template( 'form-fields/' . $field['type'] . '-field.php', array( 'key' => $key, 'field' => $field ) ); ?>				</div>			</fieldset>		<?php endforeach; ?>	</div>	<div class="ticket-fields-form basic-info-form create-event-cart">		<h3 class="section-title"><?php _e( 'Create ticket', 'wp-event-manager' ); ?></h3>        <div class="clear"></div>		<div class="create-ticket-option-btn ticketinner form-group">			<div class="ticketbuttonwpr">				<?php 						foreach ( $ticketsfields as $key => $field ) : 					$customclass = ''; /*Add custom Class */					if(in_array($key, $datedfields['datefields'])){ 						$customclass =' field_mideam calicon'; 					}					if(in_array($key, $datedfields['timefields'])){						$customclass =' field_small';					}				?>				<div class="field field-tickets">					<?php get_event_manager_template( 'form-fields/' . $field['type'] . '-field.php', array( 'key' => $key, 'field' => $field ) ); ?>				</div>				<?php endforeach; ?>			</div>            <div class="clear"></div>			<div class="form-group ticketwprs">                <?php                if(isset($_GET['action']) && $_GET['action'] == 'edit') {                    foreach ($ticketsfields as $key => $field) :                        $customclass = ''; /*Add custom Class */                        if (in_array($key, $datedfields['datefields'])) {                            $customclass = ' field_mideam calicon';                        }                        if (in_array($key, $datedfields['timefields'])) {                            $customclass = ' field_small';                        }                        ?>                        <!--<div class="field field-tickets">-->                            <?php get_event_manager_template('form-fields/' . $field['type'] . '-value-field.php', array('key' => $key, 'field' => $field)); ?>                        <!--</div>-->                    <?php endforeach;                } ?>            </div>		</div>	</div>	<div class="design-fields-form basic-info-form create-event-cart">		<h3 class="section-title"><?php _e( 'Design your event', 'wp-event-manager' ); ?></h3>        <div class="clear"></div>        <div class="design-your-event">		<?php		foreach ( $designfields as $key => $field ) :			$customclass = ''; /*Add custom Class */			if(in_array($key, array('event_banner'))){				$customclass =' col-lg-8 pullrightinner';			}			if(in_array($key, array('event_social'))){				$customclass =' col-lg-4';			}		?>            <?php if ($field['type'] == 'file'){            $field['ajax'] = 1;            $field['allowed_mime_types'] =array('jpg' => 'image/jpeg','jpeg' => 'image/jpeg','gif' => 'image/gif','png' => 'image/png');            ?>			<fieldset class="form-group fieldset-<?php esc_attr_e( $key ); ?><?php esc_attr_e( $customclass ); ?>">                <div class="custom-browse-file">                    <div class="browse-pluse-icon"><img src="<?php echo get_template_directory_uri(); ?>/assets/images/browse-pluse-icon.svg" alt=""></div>                    <label for="<?php esc_attr_e( $key ); ?>"><?php echo $field['label']; ?></label>                    <?php get_event_manager_template( 'form-fields/' . $field['type'] . '-field.php', array( 'key' => $key, 'field' => $field ) ); ?>                </div>			</fieldset>            <?php } else { ?>            <fieldset class="form-group fieldset-<?php esc_attr_e( $key ); ?><?php esc_attr_e( $customclass ); ?>">                <div class="fieldlabelinfo">                    <label for="<?php esc_attr_e( $key ); ?>"><?php echo $field['label'] . apply_filters( 'submit_event_form_required_label', $field['required'] ? '<span class="require-field">*</span>' : ' <small>' . __( '(optional)', 'wp-event-manager' ) . '</small>', $field ); ?></label>                    <?php                    if(array_key_exists($key, $introtext)){                        echo '<span class="introtext">'.$introtext[$key].'</span>';                    }                    ?>                </div>                <div class="field <?php echo $field['required'] ? 'required-field' : ''; ?>">                    <?php get_event_manager_template( 'form-fields/' . $field['type'] . '-field.php', array( 'key' => $key, 'field' => $field ) ); ?>                </div>            </fieldset>            <?php } ?>		<?php endforeach; ?>        </div>	</div>		<?php do_action( 'submit_event_form_event_fields_end' ); ?>		<!-- Organizer Information Fields -->		<?php if ( $organizer_fields ) : ?>			<div class="organizer_fields-form basic-info-form create-event-cart">				<h3 class="section-title"><?php _e( 'Organizer Details', 'wp-event-manager' ); ?></h3>				<?php do_action( 'submit_event_form_organizer_fields_start' ); ?>				<?php foreach ( $organizer_fields as $key => $field ) : ?>					<fieldset class="form-group fieldset-<?php esc_attr_e( $key ); ?>">						<div class="fieldlabelinfo">							<label for="<?php esc_attr_e( $key ); ?>"><?php echo $field['label'] . apply_filters( 'submit_event_form_required_label', $field['required'] ?'<span class="require-field">*</span>' : ' <small>' . __( '(optional)', 'wp-event-manager' ) . '</small>', $field ); ?></label>						</div>						<div class="field <?php echo $field['required'] ? 'required-field' : ''; ?>">							<?php get_event_manager_template( 'form-fields/' . $field['type'] . '-field.php', array( 'key' => $key, 'field' => $field ) ); ?>						</div>					</fieldset>				<?php endforeach; ?>				<?php do_action( 'submit_event_form_organizer_fields_end' ); ?>			</div>		<?php endif; ?>		<p>			<input type="hidden" name="event_manager_form" value="<?php echo $form; ?>" />			<input type="hidden" name="event_id" value="<?php echo esc_attr( $event_id ); ?>" />			<input type="hidden" name="step" value="<?php echo esc_attr( $step ); ?>" />			<input type="submit" name="submit_event" class="btn defulat-custom-btn" value="<?php esc_attr_e( $submit_button_text ); ?>" />		</p>	<?php else : ?>		  <?php do_action( 'submit_event_form_disabled' ); ?>	  	<?php endif; ?></form></div>    </div></div>